mapper.select:
SELECT wordId
FROM record
WHERE nextDate = #{date}
  and openid = #{openid}

SELECT wordId
FROM record
WHERE nextDate = #{date}
  and familiarCount = -1
  and openid = #{openid}

SELECT *
FROM record
where openid = #{openid}
  and wordId = #{wordId}

SELECT familiar
FROM record
where openid = #{openid}
  and wordId = #{wordId}
  and date = #{date}

SELECT *
FROM record
where openid = #{openid}
  and date = #{date}
group by wordId

SELECT *
FROM record
where openid = #{openid}
  and listId = #{listId}
ORDER BY wordId
